import React from 'react';
import './style.css';

const Card = () => {
    let content;
    if(props.faceUp) {
      content = this.props.content;
    } else {
      content = ''
    }
    return (
      <div onClick={props.flip} className={`Card ${props.faceUp ? 'face-up': ''}`}>
        {content}
      </div>
    )
}

export default Card;
