import './style.css';

function Card ({flip, content, faceUp}){
    let contentt;
    if(faceUp) {
      contentt = content;
    } else {
      contentt = ''
    }
    return (
      <div onClick={flip} className={`Card ${faceUp ? 'face-up': ''}`}>
        {content}
      </div>
    )
}

export default Card;
