   import React from 'react';
import './style.css';

class Card extends React.Component {
  render() {
    let content;
    if(this.props.faceUp) {
      content = this.props.content;
    } else {
      content = ''
    }
    return (
      <div onClick={this.props.flip} className={`Card ${this.props.faceUp ? 'face-up': ''}`}>
        {content}
      </div>
    )
  }
}

export default Card;
