import React, { useState, useEffect } from "react";
import Card from "../Card";
import "./style.css";

function Board({ options }) {
  const [game, setGame] = useState([]);
  const [firstCard, setFirstCard] = useState(null);

  const fronts = [
    "ðŸ‘¹",
    "ðŸ‘¿",
    "ðŸ§ ",
    "ðŸš—",
    "ðŸš²",
    "ðŸš‚",
    "ðŸ§¸",
    "ðŸ¤¨",
    "ðŸ¤Ÿ",
    "â™¥ï¸",
    "ðŸ¦Ÿ",
    "ðŸ¦¨",
  ];

  useEffect(() => {
    const newGame = [];
    for (let i = 0; i < options / 2; i++) {
      const firstOption = {
        id: 2 * i,
        frontId: i,
        content: fronts[i],
        faceUp: false,
        matched: false,
      };
      const secondOption = {
        id: 2 * i + 1,
        frontId: i,
        content: fronts[i],
        faceUp: false,
        matched: false,
      };

      newGame.push(firstOption);
      newGame.push(secondOption);
    }

    const shuffledGame = newGame.sort(() => Math.random() - 0.5);
    setGame(shuffledGame);
    console.log("arr", shuffledGame);
  }, []);

  const flipCardTo = (cardIdx, faceUp) => {
    setGame(
      game.map((f, i) => {
        if (i === cardIdx) {
          return {
            content: f.content,
            faceUp: !f.faceUp,
          };
        } else {
          return f;
        }
      })
    );
    console.log(ga);
  };

  const flip = (cardIdx) => {
    if (firstCard === null) {
      setFirstCard(cardIdx);
    } else {
      const firstCardContent = game[firstCard].content;
      const secondCardContent = game[cardIdx].content;
      if (firstCardContent === secondCardContent) {
        setFirstCard(null);
      } else {
        setTimeout(() => {
          flipCardTo(firstCard, false);
          flipCardTo(cardIdx, false);
          setFirstCard(null);
        }, 1000);
      }
    }

    flipCardTo(cardIdx, !game[cardIdx].faceUp);
  };

  console.log(firstCard);

  if (game.length === 0) return <div>loading... </div>;
  else {
  return game.map((f, i) => {
    return (
      <div className="Board">
        <Card
          key={i}
          flip={() => {flip(i);}}
          content={f.content}
          faceUp={f.faceUp}
        />
      </div>
    );
  });
}
}

export default Board;
