import React, { useState } from 'react';
import Card from '../Card';
import './style.css';

function Board (){

    let [deck, setDeck] = useState([]);
    const [firstCard, setFirstCard] = useState(null);

    const fronts = [
      'ðŸ’©',
      'ðŸ‘¹',
      'ðŸ‘¿',
      'ðŸ§ ',
      'ðŸš—',
      'ðŸš²',
      'ðŸš‚',
      'ðŸ§¸',
      'ðŸ¤¨',
      'ðŸ¤Ÿ',
      'â™¥ï¸',
      'ðŸ¦Ÿ',
      'ðŸ¦¨',
    ]

     deck = fronts
      .concat(fronts)
      .sort(() => Math.random() - 0.5)
      .map(item => {
        return {
          content: item,
          faceUp: false,
        }
      })


  const flipCardTo= (cardIdx, faceUp) => {
    setDeck(
        deck.map((f, i) => {
        if(i === cardIdx) {
          return {
            content: f.content,
            faceUp: !f.faceUp,
          }
        } else {
          return f;
        }
      })
    )
  }

  const flip = (cardIdx) => {
    if(firstCard === null) {
      setFirstCard( cardIdx );
    } else {
      const firstCardContent = deck[firstCard].content;
      const secondCardContent = deck[cardIdx].content;
      if(firstCardContent === secondCardContent) {
        setFirstCard(null);
      } else {
        setTimeout(() => {
          flipCardTo(firstCard, false)
          flipCardTo(cardIdx, false)
          setFirstCard(null);
        }, 1000)
      }
    }

    flipCardTo(cardIdx, !deck[cardIdx].faceUp)
  }

    console.log(firstCard);
    return (
      deck.map((f, i) => {
        return (<div className="Board">
          <Card
            flip={() => {flip(i)}}
            content={f.content}
            faceUp={f.faceUp} />
        </div>)
      })
    )

}

export default Board;
